import React, { Component } from 'react'
import axios from 'axios';


export default class Books extends Component {

  state = {
    user: '',
    order: [],
    stock: []
  }

  componentWillMount() {
    this.getStock();
    this.getOrder();
  }

  returnOrder = (bookId) => {
    const apiBaseUrl = "http://localhost:3000/api";
    const payload = {
      session: localStorage.getItem('userId'),
      bookId
    }
    axios.post(apiBaseUrl + '/return', payload)
    .then(response => {
      console.log(response);
    })
    .catch(err => {
      console.log(err.message);
    })
  }

  // view books in order, plus return
  displayOrder = () => {
    const books = this.state.order;
    console.log(books);
    const bookItems = books.map(book => 
      <li className="collection-item" key={book._id}>
        {book.bookDetail.title} <button onClick={() => this.returnOrder(book._id)}>Return</button>
        </li>);
    return(
      <div>You order:
        <ul className="collection">
        {bookItems}
        </ul>
      </div>
    )
  }

  getOrder = () => {
    const apiBaseUrl = "http://localhost:3000/api";
    const payload = {
      userId: localStorage.getItem('userId')
    };
    axios.post(apiBaseUrl + '/viewOrder', payload)
      .then(response => {
        console.log(response);
        if(response && response.data) {
          this.setState({
            order: response.data.books
          })
          console.log(response.data.books);
        }
      })
      .catch(err => {
        console.log(err.message);
      })
  }

  getStock = () => {
    axios.get("http://localhost:3000/api/book/inStock")
    .then(response => {
      if(response && response.data) {
        this.setState({
          stock: response.data.book
        })
      }
    })
    .catch(err => {
      console.log(err.message);
    })
  }

  orderBook = (bookId) => {

  }

  // order books
  displayStock = () => {
    const books = this.state.order;
    const bookItems = books.map(book => 
      <li className="collection-item" key={book._id}>
        {book.title} <button onClick={() => this.orderBook(book._id)}>Order</button>
        </li>);
    // bunch of functions
    return(
      <div>
        <ul className="collection">
          {bookItems}
        </ul>
        <input></input>
        <button>Order a book</button>
      </div>
    )
  }

  render() {
    return (
      <div className="ms-Grid-row">
        <div  className="ms-Grid-col ms-sm6 ms-md4 ms-lg2">
          {this.displayOrder()}
        </div>
        <div className="ms-Grid-col ms-sm6 ms-md4 ms-lg2">
          {/* {this.displayStock()} */}
         {this.displayStock()}
        </div>
      </div>
    )
  }
}
