import React, { Component } from 'react'
import axios from 'axios';
import { List, ListItem } from '@material-ui/core';

export default class Books extends Component {

  state = {
    user: '',
    books: []
  }

  componentWillMount() {
    const apiBaseUrl = "http://localhost:3000/api";
    const payload = {
      userId: localStorage.getItem('userId')
    };
    axios.post(apiBaseUrl + '/viewOrder', payload)
      .then(response => {
        console.log(response);
        if(response) {
          this.setState({
            books: response.data.books
          })
        }
      })
      .catch(err => {
        console.log(err.message);
      })
  }

  // view books in order, plus return
  displayOrder = () => {
    const books = this.state.books;
    console.log(books);
    const bookItems = books.map(book => <ListItem key={book._id}>{book.bookDetail.title}</ListItem>);
    return(
      <div>
        <List>
        {bookItems}
        </List>
      </div>
    )
  }

  // order books
  orderBook = () => {
    // bunch of functions
    return(
      <div>
        <input></input>
        <button>Order a book</button>
      </div>
    )
  }

  render() {
    return (
      <div>
        <div>
          {this.displayOrder()}
        </div>
        <div>
         {this.orderBook()}
        </div>
      </div>
    )
  }
}
