import React, { Component } from "react";
import axios from "axios";
import Grid from '@material-ui/core/Grid';

export default class Books extends Component {
  state = {
    user: "",
    order: [],
    stock: []
  };

  componentDidMount() {
    this.getStock();
    this.getOrder();
  }

  getOrder = () => {
    const apiBaseUrl = "http://localhost:3000/api";
    const payload = {
      userId: localStorage.getItem("userId")
    };
    axios
      .post(apiBaseUrl + "/viewOrder", payload)
      .then(response => {
        if (response && response.data) {
          console.log("ORDRE, if same isbn, do not display", response.data.books);
          this.setState({
            order: response.data.books
          });
        }
      })
      .catch(err => {
        console.log(err.message);
      });
  };

  // view books in order, plus return
  displayOrder = () => {
    const books = this.state.order;
    const bookItems = books.sort().map(book => (
      <li className="collection-item" key={book._id}>
        {book.bookDetail.title}{" "}
        <button onClick={() => this.returnOrder(book._id)}>Return</button>
      </li>
    ));
    return (
      <div>
        You order:
        <ul className="collection">{bookItems}</ul>
      </div>
    );
  };

  getStock = () => {
    axios
      .get("http://localhost:3000/api/book/inStock")
      .then(response => {
        if (response) {
          this.setState({
            stock: response.data.books
          });
        }
        console.log("STOCK RESPONSE: ", response);
      })
      .catch(err => {
        console.log(err.message);
      });
  };


  orderOrReturnBook = (bookId, path) => {
    console.log("Book id", bookId);
    const apiBaseUrl = "http://localhost:3000/api";
    const payload = {
      session: localStorage.getItem("userId"),
      bookId
    };
    axios
      .post(apiBaseUrl + path, payload)
      .then(() => {
        this.getOrder();
        this.getStock();
      })
      .catch(err => {
        console.log(err.message);
      });
  };

  orderBook = (bookId) => {
    return this.orderOrReturnBook(bookId, "/order");
  }

  returnOrder = (bookId) => {
    return this.orderOrReturnBook(bookId, "/return");
  }



  // order books
  displayStock = () => {
    const books = this.state.stock;
    console.log("Order");
    const bookItems = books.sort().map(book => (
      <li className="collection-item" key={book._id}>
        {book.title}
        <button onClick={() => this.orderBook(book.bookId)}>Order</button>
      </li>
    ));
    return (
      <div>
        Â Our Stock
        <ul className="collection">{bookItems}</ul>
        <input />
        <button>Order book</button>
      </div>
    );
  };

  CenteredGrid(props) {
    const { classes } = props; 
      return (
        <div className={classes.root}>
        <Grid container spacing={24}>
            <Grid item xs={6}>
                {this.displayOrder()}
              </Grid>
              <Grid item xs={6}>
                {/* {this.displayStock()} */}
                {this.displayStock()}
              </Grid>
          </Grid>
        </div>
      );
    }
  }




const styles = theme => ({
  root: {
    flexGrow: 1,
  },
  paper: {
    padding: theme.spacing.unit * 2,
    textAlign: 'center',
    color: theme.palette.text.secondary,
  },
});
