import React, { Component } from 'react'
import axios from 'axios';


export default class Books extends Component {

  state = {
    user: '',
    order: []
  }

  componentWillMount() {
    const apiBaseUrl = "http://localhost:3000/api";
    const payload = {
      userId: localStorage.getItem('userId')
    };
    axios.post(apiBaseUrl + '/viewOrder', payload)
      .then(response => {
        console.log(response);
        if(response) {
          this.setState({
            order: response.data.books
          })
        }
      })
      .catch(err => {
        console.log(err.message);
      })
  }

  returnOrder = (bookId) => {
    const apiBaseUrl = "http://localhost:3000/api";
    const payload = {
      session: localStorage.getItem('userId'),
      bookId
    }
    axios.post(apiBaseUrl + '/return', payload)
    .then(response => {
      console.log(response);
    })
    .catch(err => {
      console.log(err.message);
    })
  }

  // view books in order, plus return
  displayOrder = () => {
    const books = this.state.order;
    console.log(books);
    const bookItems = books.map(book => 
      <li className="collection-item" key={book._id}>
        {book.bookDetail.title} <button onClick={() => this.returnOrder(book._id)}>Return</button>
        </li>);
    return(
      <div>You order:
        <ul className="collection">
        {bookItems}
        </ul>
      </div>
    )
  }

  displayStock = () => {
    const apiBaseUrl = "http://localhost:3000/api";
    axios.post(apiBaseUrl + '/book/inStock')
    .then(response => {
      console.log(response);
    })
    .catch(err => {
      console.log(err.message);
    })
  }

  // order books
  orderBook = () => {

    // bunch of functions
    return(
      <div>
        <button onClick={() => this.displayStock()}></button>
        <input></input>
        <button>Order a book</button>
      </div>
    )
  }

  render() {
    return (
      <div className="ms-Grid-row">
        <div  className="ms-Grid-col ms-sm6 ms-md4 ms-lg2">
          {this.displayOrder()}
        </div>
        <div className="ms-Grid-col ms-sm6 ms-md4 ms-lg2">
          {/* {this.displayStock()} */}
         {this.orderBook()}
        </div>
      </div>
    )
  }
}
