import React, { Component } from 'react'
import axios from 'axios';

export default class Books extends Component {

  state = {
    user: '',
    books: []
  }

  componentWillMount() {
    console.log(localStorage.getItem('userId'));
  }

  getBooks = () => {
    const apiBaseUrl = "http://localhost:3000/api";
    axios.post(apiBaseUrl + '/viewOrder', {userId: localStorage.getItem('userId')})
      .then(response => {
        console.log(response);
        if(response) {
          this.setState({
            books: response.books
          })
        }
      })
      .catch(err => {
        console.log(err.message);
      })
  }

  // view books in order, plus return
  displayOrder = () => {
    // bunch of functions
    return(
      <div>
        <ul>
          <button onClick={() => this.getBooks()}>VIEW BOOK</button>
          <li>harry Potter</li>
          <li>Dynamically rendered list</li>
          <li>asdfa</li>
          <li>asasdfdf</li>
        </ul>
      </div>
    )
  }

  // order books
  orderBook = () => {
    // bunch of functions
    return(
      <div>
        <input></input>
        <button>Order a book</button>
      </div>
    )
  }

  render() {
    return (
      <div>
        <div>
          {this.displayOrder()}
        </div>
        <div>
         {this.orderBook()}
        </div>
      </div>
    )
  }
}
