import React, { Component } from 'react'
import axios from 'axios';

export default class Books extends Component {

  state = {
    user: '',
    books: []
  }

  componentWillMount() {
    this.setState({
      user: localStorage.getItem('userId')
    })
  }

  componentDidMount() {
    const apiBaseUrl = "http://localhost:3000/api";
    axios.post(apiBaseUrl + '/viewOrder', this.state.user)
      .then(response => {
        console.log(response);
        if(response) {
          this.setState({
            books: response.books
          })
        }
      })
      .catch(err => {
        console.log(err, "DSFLJKSDJFL");
      })
      console.log(this.state.books);
  }

  // view books in order, plus return
  displayOrder = () => {
    // bunch of functions
    return(
      <div>
        <ul>
          <li>harry Potter</li>
          <li>Dynamically rendered list</li>
          <li>asdfa</li>
          <li>asasdfdf</li>
        </ul>
      </div>
    )
  }

  // order books
  orderBook = () => {
    // bunch of functions
    return(
      <div>
        <input></input>
        <button>Order a book</button>
      </div>
    )
  }

  render() {
    return (
      <div>
        <div>
          {this.displayOrder()}
        </div>
        <div>
         {this.orderBook()}
        </div>
      </div>
    )
  }
}
