import React, { Component } from 'react'
import axios from 'axios';

export default class Books extends Component {

  state = {
    books: []
  }

  componentDidMount() {
    const userId = localStorage.getItem('userId');
    const apiBaseUrl = "http://localhost:3000/api";
    const payload = {
      userId: userId
    }
    axios.post(apiBaseUrl + '/viewOrder', payload)
      .then(response => {
        console.log(response);
        if(response) {
          this.setState({
            books: response.books
          })
        }
      })
      console.log(this.state.books);
  }

  // view books in order, plus return
  displayOrder = () => {
    // bunch of functions
    return(
      <div>
        <ul>
          <li>harry Potter</li>
          <li>Dynamically rendered list</li>
          <li>asdfa</li>
          <li>asasdfdf</li>
        </ul>
      </div>
    )
  }

  // order books
  orderBook = () => {
    // bunch of functions
    return(
      <div>
        <input></input>
        <button></button>
      </div>
    )
  }

  render() {
    return (
      <div>
        <div>
          {this.displayOrder()}
        </div>
        <div>
         {this.orderBook()}
        </div>
      </div>
    )
  }
}
