import React, { Component } from "react";
import axios from "axios";

export default class Books extends Component {
  state = {
    user: "",
    order: [],
    stock: []
  };

  componentDidMount() {
    this.getStock();
    this.getOrder();
  }

  getOrder = () => {
    const apiBaseUrl = "http://localhost:3000/api";
    const payload = {
      userId: localStorage.getItem("userId")
    };
    axios
      .post(apiBaseUrl + "/viewOrder", payload)
      .then(response => {
        if (response && response.data) {
          console.log("ORDRE, if same isbn, do not display", response.data.books);
          this.setState({
            order: response.data.books
          });
        }
      })
      .catch(err => {
        console.log(err.message);
      });
  };

  // view books in order, plus return
  displayOrder = () => {
    const books = this.state.order;
    const bookItems = books.sort().map(book => (
      <li className="collection-item" key={book._id}>
        {book.bookDetail.title}{" "}
        <button onClick={() => this.returnOrder(book._id)}>Return</button>
      </li>
    ));
    return (
      <div>
        You order:
        <ul className="collection">{bookItems}</ul>
      </div>
    );
  };

  getStock = () => {
    axios
      .get("http://localhost:3000/api/book/inStock")
      .then(response => {
        if (response) {
          this.setState({
            stock: response.data.books
          });
        }
        console.log("STOCK RESPONSE: ", response);
      })
      .catch(err => {
        console.log(err.message);
      });
  };

  listStock = () => {

  }

  // findOne then order
  // return bookid
  findOne(isbn) {
    const payload = {
      isbn
    };
    axios.post("http://localhost:3000/api/findOne", payload).then(response => {
      if (response) {
        console.log(response.data.book);
        const bookId = response.data.book._id;
        this.orderBook(bookId);
      }
    });
  }

  orderBook = bookId => {
    console.log("Book id", bookId);
    const apiBaseUrl = "http://localhost:3000/api";
    const payload = {
      session: localStorage.getItem("userId"),
      bookId
    };
    axios
      .post(apiBaseUrl + "/order", payload)
      .then(response => {
        console.log(response);
      })
      .catch(err => {
        console.log(err.message);
      });
  };

  returnOrder = bookId => {
    const apiBaseUrl = "http://localhost:3000/api";
    const payload = {
      session: localStorage.getItem("userId"),
      bookId
    };
    axios
      .post(apiBaseUrl + "/return", payload)
      .then(response => {
        console.log(response);
      })
      .catch(err => {
        console.log(err.message);
      });
  };

  // order books
  displayStock = () => {
    const books = this.state.stock;
    console.log("Order");
    const bookItems = books.sort().map(book => (
      <li className="collection-item" key={book._id}>
        {book.title} - Remain: {book.total}
        <button onClick={() => this.findOne(book.isbn)}>Order</button>
      </li>
    ));
    return (
      <div>
        <ul className="collection">{bookItems}</ul>
        <input />
        <button>Order book</button>
      </div>
    );
  };

  render() {
    return (
      <div className="ms-Grid-row">
        <div className="ms-Grid-col ms-sm2 ms-md2 ms-lg2">
          {this.displayOrder()}
        </div>
        <div className="ms-Grid-col ms-sm2 ms-md2 ms-lg2">
          {/* {this.displayStock()} */}
          {this.displayStock()}
        </div>
      </div>
    );
  }
}
