import React, { Component } from "react";
import axios from "axios";

export default class Books extends Component {
  state = {
    user: "",
    order: [],
    stock: []
  };

  componentDidMount() {
    this.getStock();
    this.getOrder();
  }

  getOrder = () => {
    const apiBaseUrl = "http://localhost:3000/api";
    const payload = {
      userId: localStorage.getItem("userId")
    };
    axios
      .post(apiBaseUrl + "/viewOrder", payload)
      .then(response => {
        if (response && response.data) {

          this.setState({
            order: response.data.books
          });
        }
      })
      .catch(err => {
        alert(err.message);
      });
  };

  // view books in order, plus return
  displayOrder = () => {
    const books = this.state.order;
    const bookItems = books.sort().map(book => (
      <li className="collection-item" key={book._id}>
        {book.bookDetail.title}{" "}
        <button onClick={() => this.returnOrder(book._id)}>Return</button>
      </li>
    ));
    return (
      <div>
        You order:
        <ul className="collection">{bookItems}</ul>
      </div>
    );
  };

  getStock = () => {
    axios
      .get("http://localhost:3000/api/book/inStock")
      .then(response => {
        if (response) {
          this.setState({
            stock: response.data.books
          });
        }
        console.log("STOCK RESPONSE: ", response);
      })
      .catch(err => {
        console.log(err.message);
      });
  };

  orderOrReturnBook = (bookId, path) => {
    console.log("Book id", bookId);
    const apiBaseUrl = "http://localhost:3000/api";
    const payload = {
      session: localStorage.getItem("userId"),
      bookId
    };
    axios
      .post(apiBaseUrl + path, payload)
      .then(() => {
        this.getOrder();
        this.getStock();
      })
      .catch(err => {
        console.log(err.message);
      });
  };

  orderBook = bookId => {
    return this.orderOrReturnBook(bookId, "/order");
  };

  returnOrder = bookId => {
    return this.orderOrReturnBook(bookId, "/return");
  };

  // order books
  displayStock = () => {
    const books = this.state.stock;
    console.log("Order");
    const bookItems = books.sort().map(book => (
      <li className="collection-item" key={book._id}>
        {book.title}
        <button onClick={() => this.orderBook(book.bookId)}>Order</button>
      </li>
    ));
    return (
      <div>
        {bookItems.length > 0 ? "Books" : "We have no more books in stock"}
        <ul className="collection">{bookItems}</ul>
      </div>
    );
  };

  render() {
    return (
      <div style={style}>
        <table >
          <tr>
            <th>Your Order</th>
            <th>Our Stock</th>
          </tr>
          <tr>
            <td>
              <ul>{this.displayOrder()}</ul>
            </td>
            <td>
              <ul>{this.displayStock()}</ul>
            </td>
          </tr>
        </table>
      </div>
    );
  }
}

const style = {
  textAlign: 'center',
  listStyleType: 'none'
}
